// Generate a bank of questions for a session
const generateBank = (sessionId: number, bankId: number): Bank => {
  const sessionQuestions = questionData[sessionId] || questionData[1];
  const startIndex = (bankId - 1) * 10;
  const endIndex = startIndex + 10;
  
  const bankQuestions: Question[] = sessionQuestions.slice(startIndex, endIndex).map((q, i) => ({
    id: `${sessionId}-${bankId}-${i}`,
    text: q.text || `Strategic Question ${i + 1}`,
    choices: q.choices || ["Option A", "Option B", "Option C", "Option D"],
    correctIndex: q.correctIndex ?? 0,
    humbugInsight: q.humbugInsight || "Strategic insight pending."
  }));

  // Shuffle questions within bank for variety
  const seed = sessionId * 100 + bankId;
  const shuffled = [...bankQuestions].sort((a, b) => {
    const hashA = (parseInt(a.id.split('-')[2]) + seed) % 100;
    const hashB = (parseInt(b.id.split('-')[2]) + seed) % 100;
    return hashA - hashB;
  });

  return {
    id: bankId,
    questions: shuffled,
    completed: false,
    score: 0
  };
};

